INSERT INTO `template_customer_group_translation` (`id`, `translatable_id`, `title`, `description`, `locale`)
VALUES
	(1, 1, 'จักรยานนั่งตรง Home Use', '<p>มีโครงสร้างเหล็ก ประหยัดพื้นที่การติดตั้ง</p>\r\n\r\n<p>มีขีดจำกัดการรับน้ำหนักตัวผู้เล่น เหมาะกับผู้เล่นที่มีน้ำหนักตัวไม่มาก ไม่เกิน 90kg.</p>\r\n\r\n<p>โปรแกรมการใช้งาน ไม่ซับซ้อน ใช้งานง่าย</p>\r\n\r\n<p>Flywheel มีขนาด 4-5 กิโลกรัม</p>\r\n\r\n<p>&nbsp;</p>', 'en'),
	(2, 1, 'จักรยานนั่งตรง Home Use', '<p>มีโครงสร้างเหล็ก ประหยัดพื้นที่การติดตั้ง</p>\r\n\r\n<p>มีขีดจำกัดการรับน้ำหนักตัวผู้เล่น เหมาะกับผู้เล่นที่มีน้ำหนักตัวไม่มาก ไม่เกิน 90kg.</p>\r\n\r\n<p>โปรแกรมการใช้งาน ไม่ซับซ้อน ใช้งานง่าย</p>\r\n\r\n<p>Flywheel มีขนาด 4-5 กิโลกรัม</p>', 'th'),
	(3, 2, 'จักรยานนั่งตรง Light Commercial', '<p>มีโครงสร้างขนาดใหญ่ขึ้น ใช้งานได้หนักมากขึ้น</p>\r\n\r\n<p>รับน้ำหนักตัวผู้เล่นได้ 120kg.</p>\r\n\r\n<p>มีโปรแกรมการใช้งานมากขึ้น ออกกำลังกายได้หลากหลาย</p>\r\n\r\n<p>Flywheel มีขนาด 6-8 กิโลกรัม</p>', 'en'),
	(4, 2, 'จักรยานนั่งตรง Light Commercial', '<p>มีโครงสร้างขนาดใหญ่ขึ้น ใช้งานได้หนักมากขึ้น</p>\r\n\r\n<p>รับน้ำหนักตัวผู้เล่นได้ 120kg.</p>\r\n\r\n<p>มีโปรแกรมการใช้งานมากขึ้น ออกกำลังกายได้หลากหลาย</p>\r\n\r\n<p>Flywheel มีขนาด 6-8 กิโลกรัม</p>', 'th'),
	(5, 3, 'จักรยานนั่งตรง Commercial', '<p>มีโครงสร้างขนาดใหญ่ แข็งแรง ทนทานสามารถใช้งานต่อเนื่องได้นาน เหมาะกับฟิตเนสคลับขนาดใหญ่ หรือสถานที่ที่มีผู้ใช้บริการเป็นจำนวนมาก</p>\r\n\r\n<p>รับน้ำหนักของผู้เล่นได้มาก 150kg. ทำให้รองรับการใช้งานได้หลากหลายกลุ่มลูกค้า</p>\r\n\r\n<p>มีโปรแกรมการใช้งานครบถ้วน หลากหลายฟังก์ชั่นตอบโจทย์ทุกความต้องการ</p>\r\n\r\n<p>Flywheel มีขนาด 9 กิโลกรัมขึ้นไป</p>', 'en'),
	(6, 3, 'จักรยานนั่งตรง Commercial', '<p>มีโครงสร้างขนาดใหญ่ แข็งแรง ทนทานสามารถใช้งานต่อเนื่องได้นาน เหมาะกับฟิตเนสคลับขนาดใหญ่ หรือสถานที่ที่มีผู้ใช้บริการเป็นจำนวนมาก</p>\r\n\r\n<p>รับน้ำหนักของผู้เล่นได้มาก 150kg. ทำให้รองรับการใช้งานได้หลากหลายกลุ่มลูกค้า</p>\r\n\r\n<p>มีโปรแกรมการใช้งานครบถ้วน หลากหลายฟังก์ชั่นตอบโจทย์ทุกความต้องการ</p>\r\n\r\n<p>Flywheel มีขนาด 9 กิโลกรัมขึ้นไป</p>', 'th'),
	(7, 4, 'ลู่วิ่งไฟฟ้า Home Use', '<p>มีโครงสร้างขนาดเล็ก ประหยัดพื้นที่ในการติดตั้ง ใช้งานต่อเนื่องได้ไม่เกิน 2-3 ชั่วโมง เหมาะกับการใช้ภายในบ้าน</p>\r\n\r\n<p>มีขีดจำกัดในการรับน้ำหนักตัวผู้เล่นได้ 120kg. เหมาะกับผู้เล่นที่มีรูปร่างไม่ใหญ่มาก</p>\r\n\r\n<p>โปรแกรมการใช้งานง่าย ไม่ซับซ้อน</p>\r\n\r\n<p>กำลังมอเตอร์อยู่ในระดับไม่เกิน 1.25 - 2.0 แรงม้า</p>', 'en'),
	(8, 4, 'ลู่วิ่งไฟฟ้า Home Use', '<p>มีโครงสร้างขนาดเล็ก ประหยัดพื้นที่ในการติดตั้ง ใช้งานต่อเนื่องได้ไม่เกิน 2-3 ชั่วโมง เหมาะกับการใช้ภายในบ้าน</p>\r\n\r\n<p>มีขีดจำกัดในการรับน้ำหนักตัวผู้เล่นได้ 120kg. เหมาะกับผู้เล่นที่มีรูปร่างไม่ใหญ่มาก</p>\r\n\r\n<p>โปรแกรมการใช้งานง่าย ไม่ซับซ้อน</p>\r\n\r\n<p>กำลังมอเตอร์อยู่ในระดับไม่เกิน 1.25 - 2.0 แรงม้า</p>', 'th'),
	(9, 5, 'ลู่วิ่งไฟฟ้า Light Commercial', '<p>มีโครงสร้างขนาดใหญ่ มีความแข็งแรงมากขึ้น ใช้งานได้หนักมากขึ้น เหมาะกับการใช้งานในคอนโดหรือหมู่บ้านขนาดปานกลาง</p>\r\n\r\n<p>รองรับน้ำหนักผู้ใช้ได้ 150kg. มีพื้นที่วิ่งมากขึ้น ทำให้วิ่งได้สะดวกกว่าลู่วิ่งแกรดในบ้าน</p>\r\n\r\n<p>มีโปรแกรมการใช้งานที่เพิ่มมากขึ้น ตอบโจทย์การใช้งานที่หลากหลาย</p>\r\n\r\n<p>กำลังมอเตอร์อยู่ในระดับ 2.5-3.5 แรงม้า ใช้งานต่อเนื่องได้ถึง 4-5 ชั่วโมงขึ้นไป</p>', 'en'),
	(10, 5, 'ลู่วิ่งไฟฟ้า Light Commercial', '<p>มีโครงสร้างขนาดใหญ่ มีความแข็งแรงมากขึ้น ใช้งานได้หนักมากขึ้น เหมาะกับการใช้งานในคอนโดหรือหมู่บ้านขนาดปานกลาง</p>\r\n\r\n<p>รองรับน้ำหนักผู้ใช้ได้ 150kg. มีพื้นที่วิ่งมากขึ้น ทำให้วิ่งได้สะดวกกว่าลู่วิ่งแกรดในบ้าน</p>\r\n\r\n<p>มีโปรแกรมการใช้งานที่เพิ่มมากขึ้น ตอบโจทย์การใช้งานที่หลากหลาย</p>\r\n\r\n<p>กำลังมอเตอร์อยู่ในระดับ 2.5-3.5 แรงม้า ใช้งานต่อเนื่องได้ถึง 4-5 ชั่วโมงขึ้นไป</p>', 'th'),
	(11, 6, 'ลู่วิ่งไฟฟ้า Commercial', '<p>มีโครงสร้างขนาดใหญ่ ถูกออกแบบสำหรับรองรับการใช้งานที่หนักโดยตรง ใช้งานต่อเนื่องได้ตลอดทั้งวัน เหมาะกับการใช้งานในฟิตเนสคลับขนาดใหญ่ ที่มีพื้นที่มาก หรือสถานที่ที่มีจำนวนผู้ใช้งานมากต่อวัน</p>\r\n\r\n<p>รองรับน้ำหนักผู้ใช้ได้ถึง 200 กิโลกรัมขึ้นไป</p>\r\n\r\n<p>มีโปรแกรมการใช้งานครบถ้วน ตอบโจทย์กลุ่มคนที่ออกกำลังกายทั่วไป หรือนักกีฬา</p>\r\n\r\n<p>กำลังมอเตอร์ขนาด 4.0 แรงม้าขึ้นไป</p>', 'en'),
	(12, 6, 'ลู่วิ่งไฟฟ้า Commercial', '<p>มีโครงสร้างขนาดใหญ่ ถูกออกแบบสำหรับรองรับการใช้งานที่หนักโดยตรง ใช้งานต่อเนื่องได้ตลอดทั้งวัน เหมาะกับการใช้งานในฟิตเนสคลับขนาดใหญ่ ที่มีพื้นที่มาก หรือสถานที่ที่มีจำนวนผู้ใช้งานมากต่อวัน</p>\r\n\r\n<p>รองรับน้ำหนักผู้ใช้ได้ถึง 200 กิโลกรัมขึ้นไป</p>\r\n\r\n<p>มีโปรแกรมการใช้งานครบถ้วน ตอบโจทย์กลุ่มคนที่ออกกำลังกายทั่วไป หรือนักกีฬา</p>\r\n\r\n<p>กำลังมอเตอร์ขนาด 4.0 แรงม้าขึ้นไป</p>', 'th'),
	(13, 7, 'เครื่องเดินวงรี Home Use', '<p>มีโครงสร้างไม่ใหญ่มาก เหมาะกับพื้นที่ขนาดเล็ก</p>\r\n\r\n<p>รองรับน้ำหนักผู้ใช้ได้ 110kg.</p>\r\n\r\n<p>โปรแกรมการใช้งานง่าย ไม่ซับซ้อน</p>\r\n\r\n<p>ชนาดจาน Flywheel หนักประมาณ 4-5 กิโลกรัม</p>', 'en'),
	(14, 7, 'เครื่องเดินวงรี Home Use', '<p>มีโครงสร้างไม่ใหญ่มาก เหมาะกับพื้นที่ขนาดเล็ก</p>\r\n\r\n<p>รองรับน้ำหนักผู้ใช้ได้ 110kg.</p>\r\n\r\n<p>โปรแกรมการใช้งานง่าย ไม่ซับซ้อน</p>\r\n\r\n<p>ชนาดจาน Flywheel หนักประมาณ 4-5 กิโลกรัม</p>', 'th'),
	(15, 8, 'เครื่องเดินวงรี Light Commercial', '<p>โครงสร้างมีขนาดใหญ่ขึ้น ช่วงรอบการก้าวกว้างช่วยให้ปั่นได้นุ่มนวลขึ้น เหมาะกับการใช้งานต่อเนื่องแต่ไม่ตลอดทั้งวัน เช่น ใน ฟิตเนสคลับชนาดเล็ก คอนโดมิเนียม หรือหมู่บ้านจัดสรรขนาดกลาง</p>\r\n\r\n<p>รองรับน้ำหนักตัวผู้ใช้งานได้ 130kg.</p>\r\n\r\n<p>ขนาดจาน Flywheel หนักประมาณ 9 กิโลกรัม</p>', 'en'),
	(16, 8, 'เครื่องเดินวงรี Light Commercial', '<p>โครงสร้างมีขนาดใหญ่ขึ้น ช่วงรอบการก้าวกว้างช่วยให้ปั่นได้นุ่มนวลขึ้น เหมาะกับการใช้งานต่อเนื่องแต่ไม่ตลอดทั้งวัน เช่น ใน ฟิตเนสคลับชนาดเล็ก คอนโดมิเนียม หรือหมู่บ้านจัดสรรขนาดกลาง</p>\r\n\r\n<p>รองรับน้ำหนักตัวผู้ใช้งานได้ 130kg.</p>\r\n\r\n<p>ขนาดจาน Flywheel หนักประมาณ 9 กิโลกรัม</p>', 'th'),
	(17, 9, 'เครื่องเดินวงรี Commercial', '<p>โครงสร้างขนาดใหญ่ ช่วงรอบการก้าวกว้างปั่นได้นุ่มนวล ไม่สะดุดเหมาะกับการใช้งานในฟิตเนสคลับโดยตรง หรือสถานที่ที่มีผู้ใช้งานต่อเนื่อง จำนวนมากต่อวัน</p>\r\n\r\n<p>มีโปรแกรมการใช้งานที่หลากหลาย ตอบโจทย์ผู้ใช้งานทั้งกลุ่มคนออกกำลังกายทั่วไปหรือนักกีฬา</p>\r\n\r\n<p>รองรับน้ำหนักตัวผู้ใช้งานได้ 150kg.</p>\r\n\r\n<p>ขนาดจาน Flywheel หนักประมาณ 6-8 กิโลกรัม</p>', 'en'),
	(18, 9, 'เครื่องเดินวงรี Commercial', '<p>โครงสร้างขนาดใหญ่ ช่วงรอบการก้าวกว้างปั่นได้นุ่มนวล ไม่สะดุดเหมาะกับการใช้งานในฟิตเนสคลับโดยตรง หรือสถานที่ที่มีผู้ใช้งานต่อเนื่อง จำนวนมากต่อวัน</p>\r\n\r\n<p>มีโปรแกรมการใช้งานที่หลากหลาย ตอบโจทย์ผู้ใช้งานทั้งกลุ่มคนออกกำลังกายทั่วไปหรือนักกีฬา</p>\r\n\r\n<p>รองรับน้ำหนักตัวผู้ใช้งานได้ 150kg.</p>\r\n\r\n<p>ขนาดจาน Flywheel หนักประมาณ 6-8 กิโลกรัม</p>', 'th'),
	(19, 10, 'เครื่องฝึกกล้ามเนื้อ Home Use', '<p>ออกแบบมาเพื่อการใช้งานที่ไม่หนักมาก ชุดน้ำหนักที่ใช้เล่นอยู่ไม่เกิน 75 กิโลกรัม อาจเป็นชุดยิมที่ใช้ฝึกเฉพาะส่วนหรือหลายส่วนก็ได้ ตัวเครื่องมีขนาดไม่หนักมาก ใช้พื้นที่ในการวางไม่มาก ประหยัดพื้นที่ สามารถตั้งภายในบ้านได้</p>', 'en'),
	(20, 10, 'เครื่องฝึกกล้ามเนื้อ Home Use', '<p>ออกแบบมาเพื่อการใช้งานที่ไม่หนักมาก ชุดน้ำหนักที่ใช้เล่นอยู่ไม่เกิน 75 กิโลกรัม อาจเป็นชุดยิมที่ใช้ฝึกเฉพาะส่วนหรือหลายส่วนก็ได้ ตัวเครื่องมีขนาดไม่หนักมาก ใช้พื้นที่ในการวางไม่มาก ประหยัดพื้นที่ สามารถตั้งภายในบ้านได้</p>', 'th'),
	(21, 11, 'เครื่องฝึกกล้ามเนื้อ Light Commercial', '<p>โครงสร้างขนาดใหญ่ แข็งแรง ทนทานขึ้น ชุดน้ำหนักที่ใช้เล่น ประมาณ 80 - 100 กิโลกรัม การใช้งานนุ่มนวล ไม่ติดขัด ออกแรงได้เต็มที่ เหมาะกับการใช้งานต่อเนื่องแต่ไม่ตลอดทั้งวัน เช่น ฟิตเนสคลับขนาดเล็ก คอนโดมิเนียม โรงพยาบาล หรือ หมู่บ้านจัดสรรขนาดกลาง</p>', 'en'),
	(22, 11, 'เครื่องฝึกกล้ามเนื้อ Light Commercial', '<p>โครงสร้างขนาดใหญ่ แข็งแรง ทนทานขึ้น ชุดน้ำหนักที่ใช้เล่น ประมาณ 80 - 100 กิโลกรัม การใช้งานนุ่มนวล ไม่ติดขัด ออกแรงได้เต็มที่ เหมาะกับการใช้งานต่อเนื่องแต่ไม่ตลอดทั้งวัน เช่น ฟิตเนสคลับขนาดเล็ก คอนโดมิเนียม โรงพยาบาล หรือ หมู่บ้านจัดสรรขนาดกลาง</p>', 'th'),
	(23, 12, 'เครื่องฝึกกล้ามเนื้อ Commercial', '<p>โครงสร้างขนาดใหญ่ แข็งแรง ทนทาน ชุดน้ำหนักที่ใช้เล่น อย่างน้อย 80 - 100 กิโลกรัมขึ้นไป รองรับการเล่นทั้งกลุ่มคนที่ออกกำลังกายทั่วไป หรือนักกีฬาที่ต้องการฝึกในน้ำหนักที่มาก การใช้งานนุ่มนวล ไม่ติดขัด ช่วงการเล่นได้มาตรฐาน กล้ามเนื้อสามารถออกแรงได้เต็มที่ ตลอดช่วงการเคลื่อนไหว</p>\r\n\r\n<p>เหมาะสำหรับการใช้งานต่อเนื่องตลอดทั้งวัน เช่น ฟิตเนสคลับขนาดใหญ่ หรือสถานที่ที่มีคนใช้บริการเป็นจำนวนมาก ตลอดทั้งวัน</p>', 'en'),
	(24, 12, 'เครื่องฝึกกล้ามเนื้อ Commercial', '<p>โครงสร้างขนาดใหญ่ แข็งแรง ทนทาน ชุดน้ำหนักที่ใช้เล่น อย่างน้อย 80 - 100 กิโลกรัมขึ้นไป รองรับการเล่นทั้งกลุ่มคนที่ออกกำลังกายทั่วไป หรือนักกีฬาที่ต้องการฝึกในน้ำหนักที่มาก การใช้งานนุ่มนวล ไม่ติดขัด ช่วงการเล่นได้มาตรฐาน กล้ามเนื้อสามารถออกแรงได้เต็มที่ ตลอดช่วงการเคลื่อนไหว</p>\r\n\r\n<p>เหมาะสำหรับการใช้งานต่อเนื่องตลอดทั้งวัน เช่น ฟิตเนสคลับขนาดใหญ่ หรือสถานที่ที่มีคนใช้บริการเป็นจำนวนมาก ตลอดทั้งวัน</p>', 'th'),
	(25, 13, 'เครื่องฝึกกล้ามเนื้อ Free Weight', '<p>การเล่นเป็นแบบอิสระ ทำให้กล้ามเนื้อมัดเล็ก มัดใหญ่ได้ออกแรงครบถ้วน สามารถจัดเข้าไปอยู่ในกลุ่มของ Home / Light commercial / Commercial ได้ทำให้การฝึกกล้ามเนื้อเป็นไปอย่างครบถ้วน สมบูรณ์มากยิ่งขึ้นเป็นที่นิยมของสุภาพสตรี ที่ต้องการฝึกน้ำหนักไม่มาก ใช้พื้นที่ในการวางไม่มาก เมื่อเทียบกับปริมาณผู้ใช้ที่สามารถเล่นได้</p>', 'en'),
	(26, 13, 'เครื่องฝึกกล้ามเนื้อ Free Weight', '<p>การเล่นเป็นแบบอิสระ ทำให้กล้ามเนื้อมัดเล็ก มัดใหญ่ได้ออกแรงครบถ้วน สามารถจัดเข้าไปอยู่ในกลุ่มของ Home / Light commercial / Commercial ได้ทำให้การฝึกกล้ามเนื้อเป็นไปอย่างครบถ้วน สมบูรณ์มากยิ่งขึ้นเป็นที่นิยมของสุภาพสตรี ที่ต้องการฝึกน้ำหนักไม่มาก ใช้พื้นที่ในการวางไม่มาก เมื่อเทียบกับปริมาณผู้ใช้ที่สามารถเล่นได้</p>', 'th'),
	(27, 14, 'จักรยานนั่งพิง Home Use', '<p>มีโครงสร้างเหล็ก ประหยัดพื้นที่การติดตั้ง</p>\r\n\r\n<p>มีขีดจำกัดการรับน้ำหนักตัวผู้เล่น เหมาะกับผู้เล่นที่มีน้ำหนักตัวไม่มาก ไม่เกิน 90kg.</p>\r\n\r\n<p>โปรแกรมการใช้งาน ไม่ซับซ้อน ใช้งานง่าย</p>\r\n\r\n<p>Flywheel มีขนาด 4-5 กิโลกรัม</p>\r\n\r\n<p>&nbsp;</p>', 'en'),
	(28, 14, 'จักรยานนั่งพิง Home Use', '<p>มีโครงสร้างเหล็ก ประหยัดพื้นที่การติดตั้ง</p>\r\n\r\n<p>มีขีดจำกัดการรับน้ำหนักตัวผู้เล่น เหมาะกับผู้เล่นที่มีน้ำหนักตัวไม่มาก ไม่เกิน 90kg.</p>\r\n\r\n<p>โปรแกรมการใช้งาน ไม่ซับซ้อน ใช้งานง่าย</p>\r\n\r\n<p>Flywheel มีขนาด 4-5 กิโลกรัม</p>\r\n\r\n<p>&nbsp;</p>', 'th'),
	(29, 15, 'จักรยานนั่งพิง Commercial', '<p>มีโครงสร้างขนาดใหญ่ แข็งแรง ทนทานสามารถใช้งานต่อเนื่องได้นาน เหมาะกับฟิตเนสคลับขนาดใหญ่ หรือสถานที่ที่มีผู้ใช้บริการเป็นจำนวนมาก</p>\r\n\r\n<p>รับน้ำหนักของผู้เล่นได้มาก 150kg. ทำให้รองรับการใช้งานได้หลากหลายกลุ่มลูกค้า</p>\r\n\r\n<p>มีโปรแกรมการใช้งานครบถ้วน หลากหลายฟังก์ชั่นตอบโจทย์ทุกความต้องการ</p>\r\n\r\n<p>Flywheel มีขนาด 9 กิโลกรัมขึ้นไป</p>', 'en'),
	(30, 15, 'จักรยานนั่งพิง Commercial', '<p>มีโครงสร้างขนาดใหญ่ แข็งแรง ทนทานสามารถใช้งานต่อเนื่องได้นาน เหมาะกับฟิตเนสคลับขนาดใหญ่ หรือสถานที่ที่มีผู้ใช้บริการเป็นจำนวนมาก</p>\r\n\r\n<p>รับน้ำหนักของผู้เล่นได้มาก 150kg. ทำให้รองรับการใช้งานได้หลากหลายกลุ่มลูกค้า</p>\r\n\r\n<p>มีโปรแกรมการใช้งานครบถ้วน หลากหลายฟังก์ชั่นตอบโจทย์ทุกความต้องการ</p>\r\n\r\n<p>Flywheel มีขนาด 9 กิโลกรัมขึ้นไป</p>', 'th'),
	(31, 16, 'จักรยานนั่งพิง Light Commercial', '<p>มีโครงสร้างขนาดใหญ่ขึ้น ใช้งานได้หนักมากขึ้น</p>\r\n\r\n<p>รับน้ำหนักตัวผู้เล่นได้ 120kg.</p>\r\n\r\n<p>มีโปรแกรมการใช้งานมากขึ้น ออกกำลังกายได้หลากหลาย</p>\r\n\r\n<p>Flywheel มีขนาด 6-8 กิโลกรัม</p>', 'en'),
	(32, 16, 'จักรยานนั่งพิง Light Commercial', '<p>มีโครงสร้างขนาดใหญ่ขึ้น ใช้งานได้หนักมากขึ้น</p>\r\n\r\n<p>รับน้ำหนักตัวผู้เล่นได้ 120kg.</p>\r\n\r\n<p>มีโปรแกรมการใช้งานมากขึ้น ออกกำลังกายได้หลากหลาย</p>\r\n\r\n<p>Flywheel มีขนาด 6-8 กิโลกรัม</p>', 'th');
